"use strict";

(function ($) {
  $(document).ready(function () {
    svg4everybody({});
  });
  console.log('hello');
  $('.burger').on("click", function (event) {
    event.preventDefault();
    $('body').toggleClass('overflow-hidden');
    $('.header__bottom').toggleClass('open-menu');
    $('.burger .icon-burger').fadeToggle(0);
    $('.burger .icon-close').fadeToggle(0);
  });
  new Swiper('.cars-gallery .swiper', {
    // Optional parameters
    loop: true,
    speed: 1500,
    spaceBetween: 30,
    slidesPerView: 1,
    // If we need pagination
    breakpoints: {
      // when window width is >= 320px
      576: {
        slidesPerView: 1,
        spaceBetween: 20
      },
      // when window width is >= 480px
      992: {
        slidesPerView: 2,
        spaceBetween: 30
      },
      // when window width is >= 640px
      1200: {
        slidesPerView: 3,
        spaceBetween: 40
      }
    },
    pagination: {
      el: '.swiper-pagination'
    },
    // Navigation arrows
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev'
    }
  });
})(jQuery);